const jobReducer = (state: jobInitialState = initialState, action: actionProperties ) => {
    const { type, payload } = action;
    switch(type) {
        case jobConstants.SET_JOBS:
            const jobs = payload;
            state.jobs = jobs;
        break
    }

    return state
}

export default jobReducer;